import React, { useState } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Table, Alert } from "reactstrap";
import RegisterClientModal from "./RegisterClientModal"; // Importa el nuevo componente
import { useApiConstructor } from "../apiCalls/apiConstructor";
import { Customers } from "../cons/urls"

const ClientModal = ({ isOpen, onClose, onSelect }) => {
  const [name, setName] = useState("");
  const [Lastname, setLastname] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [clients, setClients] = useState([]);
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const apiCall = useApiConstructor(Customers);

  const handleSearch = async () => {
    try {
      const response = await apiCall.get(`/search`, {
        params: { name, Lastname, phone, email },
      });
      setClients(response.data);
    } catch (err) {
      console.error("Error fetching clients:", err);
    }
  };

  const handleRegister = () => {
    setShowRegisterForm(false);
    handleSearch(); // Refresca la lista de clientes despuÃ©s de registrar uno nuevo
  };

  return (
    <>
      <Modal isOpen={isOpen} toggle={onClose} size="lg">
        <ModalHeader toggle={onClose}>Seleccionar Cliente</ModalHeader>
        <ModalBody>
          {!showRegisterForm ? (
            <>
              <Input
                type="text"
                placeholder="Nombre"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="mb-2"
              />
              <Input
                type="text"
                placeholder="Apellidos"
                value={Lastname}
                onChange={(e) => setLastname(e.target.value)}
                className="mb-2"
              />
              <Input
                type="text"
                placeholder="Telefono"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
                className="mb-2"
              />
              <Input
                type="text"
                placeholder="Email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="mb-2"
              />
              <Button onClick={handleSearch} className="mb-3">Buscar</Button>
              <Button onClick={() => setShowRegisterForm(true)} color="primary" className="mb-3">Registrar nuevo cliente</Button>
              
              {clients.length === 0 && (
                <Alert color="warning">
                  No hay registros.
                </Alert>
              )}
              
              <Table striped>
                <thead>
                  <tr>
                    <th>Nombre</th>
                    <th>Email</th>
                    <th>Telefono</th>
                    <th>AcciÃ³n</th>
                  </tr>
                </thead>
                <tbody>
                  {clients.map((client) => (
                    <tr key={client.id}>
                      <td>{client.name}</td>
                      <td>{client.email}</td>
                      <td>{client.phone}</td>
                      <td>
                        <Button
                          onClick={() => onSelect(client)}
                          color="primary"
                        >
                          Seleccionar
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </>
          ) : (
            <RegisterClientModal
              isOpen={showRegisterForm}
              onClose={() => setShowRegisterForm(false)}
              onRegister={handleRegister}
            />
          )}
        </ModalBody>
        <ModalFooter>
          <Button color="secondary" onClick={onClose}>Cerrar</Button>
        </ModalFooter>
      </Modal>
    </>
  );
};

export default ClientModal;
