import React, { useState, useEffect } from "react";
import { apiOrders } from "../apiCalls/apiCalls";
import { Form, Col, FormGroup, Input, Label, Button } from "reactstrap";
import ClientModal from "./modals/ClientModal";
import ProductModal from "./modals/ProductModal";
import OrderTable from "./OrderTable";
import StoreModal from "./modals/StoreModal";

const OrderForm = () => {
  const [hawas, setHawas] = useState([]);
  const [selectedHawa, setSelectedHawa] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [discount, setDiscount] = useState(0);
  const [price, setPrice] = useState(0);
  const [available, setAvailable] = useState(false);
  const [orderList, setOrderList] = useState([]);
  const [customerData, setCustomerData] = useState({ id: "", name: "", store: "" });
  const [vendorName, setVendorName] = useState("");
  const [storeId, setStoreId] = useState("");
  const [storeName, setStoreName] = useState("");
  const [status, setStatus] = useState("pending");
  const [submissionStatus, setSubmissionStatus] = useState("");
  const [showClientModal, setShowClientModal] = useState(false);
  const [showProductModal, setShowProductModal] = useState(false);
  const [showStoreModal, setShowStoreModal] = useState(false);

  useEffect(() => {
    // Fetch HAWAs if needed
  }, []);

  const handleHawaChange = async (e) => {
    const hawa = e.target.value;
    setSelectedHawa(hawa);

    try {
      const response = await apiOrders.get(`/orders/${hawa}`);
      const { price, discount, available } = response.data;
      setPrice(price);
      setDiscount(discount);
      setAvailable(available);
    } catch (err) {
      console.error("Error fetching product data:", err);
    }
  };

  const handleAddToOrder = () => {
    if (!available) {
      alert("Product is not available.");
      return;
    }

    setOrderList([
      ...orderList,
      { hawa: selectedHawa, quantity, discount, price },
    ]);
    setSelectedHawa("");
    setQuantity(1);
  };

  const handleDeleteFromOrder = (index) => {
    const updatedOrderList = orderList.filter((_, i) => i !== index);
    setOrderList(updatedOrderList);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Get the user's IP (this may require additional setup or a service)
    const userIp = "192.168.1.1"; // Example IP, replace with actual method to get IP

    const orderDetails = {
      orderList,
      customerData,
      vendorName,
      status,
      ipUsuario: userIp,
      tiendaId: storeId,
    };

    try {
      const response = await apiOrders.post("/v1/orders", orderDetails);
      setSubmissionStatus("Order placed successfully");
    } catch (err) {
      console.error("Error placing order:", err);
      setSubmissionStatus("Error placing order");
    }
  };

  return (
    <div>
      <h2>Levantamiento de pedidos</h2>
      <br />
      <br />
      <Form onSubmit={handleSubmit}>
        <FormGroup row>
          <Label for="Customer" sm={2}>
            Cliente
          </Label>
          <Col sm={10}>
            <Input
              id="Customer"
              name="Customer"
              placeholder="Seleccionar cliente"
              type="text"
              value={customerData.name}
              readOnly
              onClick={() => setShowClientModal(true)}
            />
          </Col>
        </FormGroup>
        <FormGroup row>
          <Label for="Store" sm={2}>
            Tienda
          </Label>
          <Col sm={10}>
            <Input
              id="Store"
              name="Store"
              placeholder="Seleccionar tienda"
              type="text"
              value={storeName}
              readOnly
              onClick={() => setShowStoreModal(true)}
            />
          </Col>
        </FormGroup>
        <FormGroup row>
          <Label for="Vendor" sm={2}>
            Vendedor
          </Label>
          <Col sm={10}>
            <Input
              id="Vendor"
              name="Vendor"
              placeholder="Nombre del vendedor"
              type="text"
              value={vendorName}
              onChange={(e) => setVendorName(e.target.value)}
            />
          </Col>
        </FormGroup>
        <Button onClick={() => setShowProductModal(true)}>Agregar Producto</Button>
      </Form>
      <br />
      <OrderTable
        orderList={orderList}
        onAdd={() => setShowProductModal(true)}
        onEdit={() => setShowProductModal(true)}
        onDelete={handleDeleteFromOrder} // Pasar la función de eliminación
      />
      <div>
        <p>{submissionStatus}</p>
      </div>
      <br />
      <Button onClick={handleAddToOrder}>Generar pedido</Button>
      <br />
      <ClientModal
        isOpen={showClientModal}
        onClose={() => setShowClientModal(false)}
        onSelect={(client) => {
          setCustomerData({ id: client.id, name: client.name });
          setShowClientModal(false);
        }}
      />
      <ProductModal
        isOpen={showProductModal}
        onClose={() => setShowProductModal(false)}
        onAddToOrder={(product) => {
          setOrderList([...orderList, product]);
          setShowProductModal(false);
        }}
      />
      <StoreModal
        isOpen={showStoreModal}
        onClose={() => setShowStoreModal(false)}
        onSelect={(store) => {
          setStoreId(store.id);
          setStoreName(store.name); // Muestra el nombre de la tienda
        }}
      />
    </div>
  );
};

export default OrderForm;
