import React, { useState } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, FormFeedback } from "reactstrap";
import { useApiConstructor } from "../../apiCalls/apiConstructor";
import { UserServices } from "../../cons/urls"

const RegisterClientModal = ({ isOpen, onClose, onRegister }) => {
  const [name, setName] = useState("");
  const [Lastname, setLastname] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [errors, setErrors] = useState({});
  const apiCall = useApiConstructor(UserServices);


  // Expresiones regulares para validaciones
  const phoneRegex = /^[0-9]{7,15}$/; // Solo números, ajusta según necesidades
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const validateFields = () => {
    const errors = {};
    if (!name) errors.name = "El nombre es obligatorio.";
    if (!Lastname) errors.Lastname = "El apellido es obligatorio.";
    if (!phone) errors.phone = "El teléfono es obligatorio.";
    else if (!phoneRegex.test(phone)) errors.phone = "Número de teléfono inválido.";
    if (!email) errors.email = "El email es obligatorio.";
    else if (!emailRegex.test(email)) errors.email = "Email inválido.";
    if (!address) errors.address = "La dirección es obligatoria.";
    return errors;
  };

  const handleRegister = async () => {
    const validationErrors = validateFields();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return;
    }
    
    try {
      const newClient = { name, Lastname, phone, email, address }; 
      await apiCall.post(`/`, newClient);
      onRegister();
      onClose();
    } catch (err) {
      console.error("Error registering client:", err);
    }
  };

  // Función para restringir la entrada a solo números
  const handlePhoneChange = (e) => {
    const value = e.target.value;
    if (/^[0-9]*$/.test(value)) { // Permite solo números
      setPhone(value);
    }
  };

  return (
    <Modal isOpen={isOpen} toggle={onClose} size="lg">
      <ModalHeader toggle={onClose}>Registrar Nuevo Cliente</ModalHeader>
      <ModalBody>
        <Input
          type="text"
          placeholder="Nombre"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="mb-2"
          invalid={!!errors.name} // Muestra el estado de error
        />
        <FormFeedback>{errors.name}</FormFeedback> {/* Mensaje de error */}
        
        <Input
          type="text"
          placeholder="Apellidos"
          value={Lastname}
          onChange={(e) => setLastname(e.target.value)}
          className="mb-2"
          invalid={!!errors.Lastname} // Muestra el estado de error
        />
        <FormFeedback>{errors.Lastname}</FormFeedback> {/* Mensaje de error */}
        
        <Input
          type="tel"
          placeholder="Telefono"
          value={phone}
          onChange={handlePhoneChange}
          className="mb-2"
          invalid={!!errors.phone} // Muestra el estado de error
        />
        <FormFeedback>{errors.phone}</FormFeedback> {/* Mensaje de error */}
        
        <Input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="mb-2"
          invalid={!!errors.email} // Muestra el estado de error
        />
        <FormFeedback>{errors.email}</FormFeedback> {/* Mensaje de error */}
        
        <Input
          type="text"
          placeholder="Dirección" // Nuevo campo para dirección
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          className="mb-2"
          invalid={!!errors.address} // Muestra el estado de error
        />
        <FormFeedback>{errors.address}</FormFeedback> {/* Mensaje de error */}
      </ModalBody>
      <ModalFooter>
        <Button color="success" onClick={handleRegister}>Registrar</Button>
        <Button color="secondary" onClick={onClose}>Cancelar</Button>
      </ModalFooter>
    </Modal>
  );
};

export default RegisterClientModal;
