import React, { useState, useEffect } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Table, FormGroup, Label } from "reactstrap";
import { apiOrders } from "../../apiCalls/apiCalls";

const ProductModal = ({ isOpen, onClose, onAddToOrder }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [discount, setDiscount] = useState(0);

  useEffect(() => {
    if (searchTerm) {
      const fetchProducts = async () => {
        try {
          const response = await apiOrders.get(`/products/search?name=${searchTerm}`);
          setProducts(response.data);
        } catch (err) {
          console.error("Error fetching products:", err);
        }
      };
      fetchProducts();
    } else {
      setProducts([]);
    }
  }, [searchTerm]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSelectProduct = (product) => {
    setSelectedProduct(product);
  };

  const handleAddProductToOrder = () => {
    if (selectedProduct) {
      const orderItem = {
        hawaId: selectedProduct.id,
        nombreProducto: selectedProduct.name,
        precioLista: selectedProduct.price,
        descuento: discount,
        cantidad: quantity,
        subtotal: (selectedProduct.price - discount) * quantity,
        disponibilidad: selectedProduct.available,
      };
      onAddToOrder(orderItem);
      setSelectedProduct(null);
      setQuantity(1);
      setDiscount(0);
      onClose();
    }
  };

  const handleDiscountChange = (e) => {
    let value = Number(e.target.value);
    // Validar el descuento
    if (value < 0) {
      value = 0;
    } else if (value > 100) {
      value = 100;
    }
    setDiscount(value);
  };

  return (
    <Modal isOpen={isOpen} toggle={onClose}>
      <ModalHeader toggle={onClose}>Select Product</ModalHeader>
      <ModalBody>
        <FormGroup>
          <Input
            type="text"
            placeholder="Search by product name"
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </FormGroup>
        <Table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Price</th>
              <th>Available</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {products.length > 0 ? (
              products.map((product) => (
                <tr key={product.id}>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>{product.available ? "Yes" : "No"}</td>
                  <td>
                    <Button onClick={() => handleSelectProduct(product)}>Select</Button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4">No products found</td>
              </tr>
            )}
          </tbody>
        </Table>
        {selectedProduct && (
          <div>
            <h5>Selected Product: {selectedProduct.name}</h5>
            <FormGroup>
              <Label for="quantity">Quantity</Label>
              <Input
                id="quantity"
                type="number"
                min="1"
                placeholder="Quantity"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
              />
            </FormGroup>
            <FormGroup>
              <Label for="discount">Discount</Label>
              <Input
                id="discount"
                type="number"
                min="0"
                max="100"
                placeholder="Discount"
                value={discount}
                onChange={handleDiscountChange} // Actualiza la función para manejar el cambio en el descuento
              />
            </FormGroup>
            <Button onClick={handleAddProductToOrder}>Add to Order</Button>
          </div>
        )}
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={onClose}>Close</Button>
      </ModalFooter>
    </Modal>
  );
};

export default ProductModal;
