import React, { useState, useEffect, useRef } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Table, Spinner, Alert, Button } from "reactstrap";
import { useApiConstructor } from "../../apiCalls/apiConstructor";
import { Orders } from "../../cons/urls";

const StoreModal = ({ isOpen, onClose, onSelect }) => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  
  // Usamos un ref para almacenar si ya hemos cargado los datos
  const hasFetched = useRef(false);
  const isFetching = useRef(false);

  // Llama a useApiConstructor directamente en el cuerpo del componente
  const apiCall = useApiConstructor(Orders);

  useEffect(() => {
    const fetchStores = async () => {
      if (!isOpen || hasFetched.current || isFetching.current) {
        return; // No hace nada si el modal no está abierto o los datos ya se han cargado o si ya se está realizando una petición
      }

      isFetching.current = true;
      setLoading(true);
      setError(null);

      try {
        console.log("Fetching stores...");
        const response = await apiCall.get('/stores'); // Cambia la URL según sea necesario
        setStores(response.data);
        hasFetched.current = true; // Marca que los datos han sido cargados
      } catch (err) {
        setError("Error fetching stores");
        console.error("Error fetching stores:", err);
      } finally {
        setLoading(false);
        isFetching.current = false;
      }
    };

    fetchStores();
  }, [isOpen, apiCall]); // Dependencia de isOpen para ejecutar la lógica cuando el modal se abre

  // Resetear el estado cuando el modal se cierra
  useEffect(() => {
    if (!isOpen) {
      hasFetched.current = false; // Permitir nueva carga de datos en la próxima apertura
      setStores([]); // Limpiar los datos para la próxima vez
    }
  }, [isOpen]);

  return (
    <Modal isOpen={isOpen} toggle={onClose}>
      <ModalHeader toggle={onClose}>Seleccionar Tienda</ModalHeader>
      <ModalBody>
        {loading ? (
          <div className="text-center">
            <Spinner color="primary" />
          </div>
        ) : error ? (
          <Alert color="danger">{error}</Alert>
        ) : (
          <>
            {stores.length > 0 ? (
              <Table striped>
                <thead>
                  <tr>
                    <th>Nombre</th>
                    <th>Descripción</th>
                    <th>Acción</th>
                  </tr>
                </thead>
                <tbody>
                  {stores.map((store) => (
                    <tr key={store.id}>
                      <td>{store.name}</td>
                      <td>{store.description}</td>
                      <td>
                        <Button
                          color="primary"
                          onClick={() => {
                            onSelect(store);
                            onClose();
                          }}
                        >
                          Seleccionar
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            ) : (
              <p>No hay tiendas disponibles.</p>
            )}
          </>
        )}
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={onClose}>Cerrar</Button>
      </ModalFooter>
    </Modal>
  );
};

export default StoreModal;
