import { createSlice } from '@reduxjs/toolkit';
import apiHook from '../../apiCalls/apiHook';
import useAuth from '../../hooks/useAuth';

export const ordersSlice = createSlice({
  name: 'orders',
  initialState: {
    list: [],
    status: 'idle',
  },
  reducers: {
    setOrders: (state, action) => {
      state.list = action.payload;
    },
    addOrder: (state, action) => {
      state.list.push(action.payload);
    },
    updateOrder: (state, action) => {
      const index = state.list.findIndex(order => order.id === action.payload.id);
      if (index >= 0) {
        state.list[index] = action.payload;
      }
    },
  },
});

export const { setOrders, addOrder, updateOrder } = ordersSlice.actions;

export const fetchOrders = () => async (dispatch) => {
  try {
    const token = useAuth(); // Obtén el token del hook
    const response = await apiHook.get('/orders', {
      headers: {
        'Authorization': `Bearer ${token}` // Añade el token a las cabeceras
      }
    });
    dispatch(setOrders(response.data));
  } catch (err) {
    console.error("Error fetching orders:", err);
  }
};

export const confirmOrder = (orderId) => async (dispatch) => {
  try {
    const token = useAuth(); // Obtén el token del hook
    const response = await apiHook.patch(`/orders/${orderId}/confirm`, {}, {
      headers: {
        'Authorization': `Bearer ${token}` // Añade el token a las cabeceras
      }
    });
    dispatch(updateOrder(response.data));
  } catch (err) {
    console.error("Error confirming order:", err);
  }
};

export const cancelOrder = (orderId) => async (dispatch) => {
  try {
    const token = useAuth(); // Obtén el token del hook
    const response = await apiHook.patch(`/orders/${orderId}/cancel`, {}, {
      headers: {
        'Authorization': `Bearer ${token}` // Añade el token a las cabeceras
      }
    });
    dispatch(updateOrder(response.data));
  } catch (err) {
    console.error("Error canceling order:", err);
  }
};

export default ordersSlice.reducer;
