import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchOrders, confirmOrder, cancelOrder } from '../../features/orders/ordersSlice';
import OrderDetail from './OrderDetail'; // Asegúrate de tener un componente para mostrar el detalle del pedido
import { Table, Button } from "reactstrap";


const OrderList = () => {
  const dispatch = useDispatch();
  const orders = useSelector((state) => state.orders.list);
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    dispatch(fetchOrders());
  }, [dispatch]);

  const handleSelectOrder = (order) => {
    // Si ya hay un pedido seleccionado y es el mismo que el que se está intentando seleccionar, oculta los detalles
    if (selectedOrder && selectedOrder.id === order.id) {
      setSelectedOrder(null);
    } else {
      setSelectedOrder(order);
    }
  };

  const handleConfirmOrder = (orderId) => {
    dispatch(confirmOrder(orderId));
  };

  const handleCancelOrder = (orderId) => {
    dispatch(cancelOrder(orderId));
  };

  return (
    <div>
      <Table striped>
        <thead>
          <tr>
            <th>ID</th>
            <th>Customer</th>
            <th>Sales Person</th>
            <th>Status</th>
            <th>Create Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.length > 0 ? (
            orders.map((order) => (
              <tr key={order.id}>
                <td>{order.id}</td>
                <td>{order.customer}</td>
                <td>{order.salesPersonName}</td>
                <td>{order.status}</td>
                <td>{new Date(order.createDate).toLocaleString()}</td>
                <td>
                  <Button color="info" onClick={() => handleSelectOrder(order)}>
                    {selectedOrder && selectedOrder.id === order.id ? 'Ocultar Detalles' : 'Ver Detalles'}
                  </Button>
                  <Button color="info" onClick={() => handleConfirmOrder(order.id)}>Confirmar</Button>
                  <Button color="info" onClick={() => handleCancelOrder(order.id)}>Cancelar</Button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="6">No hay pedidos disponibles</td>
            </tr>
          )}
        </tbody>
      </Table>
      {selectedOrder && <OrderDetail order={selectedOrder} />}
    </div>
  );
};

export default OrderList;
