import React, { useState } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Table } from "reactstrap";
import { apiCustomers } from "../../apiCalls/apiCalls"; // Asegúrate de que la ruta sea correcta

const ClientModal = ({ isOpen, onClose, onSelect }) => {
  const [name, setName] = useState("");
  const [Lastname, setLastname] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [clients, setClients] = useState([]);
  const [showRegisterForm, setShowRegisterForm] = useState(false); // Estado para mostrar el formulario de registro

  const handleSearch = async () => {
    try {
      const response = await apiCustomers.get(`/search`, {
        params: { name, Lastname, phone, email },
      });
      setClients(response.data);
    } catch (err) {
      console.error("Error fetching clients:", err);
    }
  };

  const handleRegister = async () => {
    try {
      const newClient = { name, Lastname, phone, email };
      await apiCustomers.post(`/register`, newClient); // Cambia la URL según tu configuración del backend
      setShowRegisterForm(false);
      setName("");
      setLastname("");
      setPhone("");
      setEmail("");
      handleSearch(); // Refresca la lista de clientes
    } catch (err) {
      console.error("Error registering client:", err);
    }
  };

  return (
    <Modal isOpen={isOpen} toggle={onClose} size="lg">
      <ModalHeader toggle={onClose}>Select Client</ModalHeader>
      <ModalBody>
        {!showRegisterForm ? (
          <>
            <Input
              type="text"
              placeholder="Nombre"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="mb-2"
            />
            <Input
              type="text"
              placeholder="Apellidos"
              value={Lastname}
              onChange={(e) => setLastname(e.target.value)}
              className="mb-2"
            />
            <Input
              type="text"
              placeholder="Telefono"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="mb-2"
            />
            <Input
              type="text"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="mb-2"
            />
            <Button onClick={handleSearch} className="mb-3">Buscar</Button>
            <Button onClick={() => setShowRegisterForm(true)} color="primary" className="mb-3">Registrar nuevo cliente</Button>
            <Table striped>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Email</th>
                  <th>Telefono</th>
                  <th>Acción</th>
                </tr>
              </thead>
              <tbody>
                {clients.map((client) => (
                  <tr key={client.id}>
                    <td>{client.name}</td>
                    <td>{client.email}</td>
                    <td>{client.phone}</td>
                    <td>
                      <Button
                        onClick={() => onSelect(client)}
                        color="primary"
                      >
                        Seleccionar
                      </Button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </>
        ) : (
          <>
            <h4>Registrar Nuevo Cliente</h4>
            <Input
              type="text"
              placeholder="Nombre"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="mb-2"
            />
            <Input
              type="text"
              placeholder="Apellidos"
              value={Lastname}
              onChange={(e) => setLastname(e.target.value)}
              className="mb-2"
            />
            <Input
              type="text"
              placeholder="Telefono"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="mb-2"
            />
            <Input
              type="text"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="mb-2"
            />
            <Button onClick={handleRegister} color="success" className="mb-3">Registrar</Button>
            <Button onClick={() => setShowRegisterForm(false)} color="secondary">Volver</Button>
          </>
        )}
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={onClose}>Cerrar</Button>
      </ModalFooter>
    </Modal>
  );
};

export default ClientModal;
