import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../features/auth/authSlice";
import { useNavigate } from "react-router-dom";
import { apiUserServices } from "../apiCalls/apiCalls";
import { Form, FormGroup, Input, Label, Button } from "reactstrap";
import jwtDecode from "jwt-decode"; // Asegúrate de instalar jwt-decode

const LoginForm = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await apiUserServices.post(
        `/authenticate?userName=${username}&password=${password}`
      );

      const token = response.data.token;
      const decodedToken = jwtDecode(token);
      const user = decodedToken.username; // Asegúrate de que el nombre de usuario esté en el token

      dispatch(login({ token, username: user }));
      navigate("/");
    } catch (err) {
      console.error("Error during login:", err);
    }
  };

  return (
    <div className="login-container">
      <Form onSubmit={handleSubmit}>
        <FormGroup floating>
          <Input
            id="username"
            name="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            type="text"
          />
          <Label for="username">Usuario</Label>
        </FormGroup>{" "}
        <FormGroup floating>
          <Input
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            type="password"
          />
          <Label for="examplePassword">Password</Label>
        </FormGroup>{" "}
        <Button block color="primary">Iniciar sesión</Button> 
      </Form>
    </div>
  );
};

export default LoginForm;
