// src/features/rootReducer.js
import { combineReducers } from 'redux';
import authReducer from './auth/authSlice'; // Ajusta según la ubicación de tu authSlice
import ordersReducer from './orders/ordersSlice'; // Ajusta según la ubicación de tu ordersSlice

const rootReducer = combineReducers({
  auth: authReducer,
  orders: ordersReducer,
});

export default rootReducer;
