import axios from 'axios';
import store from '../store'; // Asegúrate de importar el store correctamente
import { selectToken } from '../features/auth/authSlice'; // Selector para obtener el token
import { Orders } from "../../cons/urls";


const apiHook = axios.create({
  baseURL: Orders, 
  headers: {
    'Content-Type': 'application/json',
  },
});

// Interceptor para agregar el token a las solicitudes
apiHook.interceptors.request.use(
  (config) => {
    const state = store.getState(); // Obtén el estado del store
    const token = selectToken(state); // Usa el selector para obtener el token
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export default apiHook;
