import React, { useState } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Table } from "reactstrap";
import { apiCustomers } from "../../apiCalls/apiCalls"; // Asegúrate de que la ruta sea correcta

const ClientModal = ({ isOpen, onClose, onSelect }) => {
  const [name, setName] = useState("");
  const [Lastname, setLastname] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [clients, setClients] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await apiCustomers.get(`/search`, {
        params: { name, Lastname, phone, email },
      });
      setClients(response.data);
    } catch (err) {
      console.error("Error fetching clients:", err);
    }
  };

  return (
    <Modal 
      isOpen={isOpen} 
      toggle={onClose}
      size="lg" // Tamaño grande
      style={{ maxWidth: '90%', width: '800px' }} // Ajuste adicional del tamaño
    >
      <ModalHeader toggle={onClose}>Select Client</ModalHeader>
      <ModalBody>
        <Input
          type="text"
          placeholder="Nombre"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="mb-2"
        />
        <Input
          type="text"
          placeholder="Apellidos"
          value={Lastname}
          onChange={(e) => setLastname(e.target.value)}
          className="mb-2"
        />
        <Input
          type="text"
          placeholder="Telefono"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          className="mb-2"
        />
        <Input
          type="text"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="mb-2"
        />
        <Button onClick={handleSearch} className="mb-3">Buscar</Button>

        <Table striped>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Email</th>
              <th>Telefono</th>
              <th>Acción</th>
            </tr>
          </thead>
          <tbody>
            {clients.map((client) => (
              <tr key={client.id}>
                <td>{client.name}</td>
                <td>{client.email}</td>
                <td>{client.phone}</td>
                <td>
                  <Button
                    onClick={() => onSelect(client)}
                    color="primary"
                  >
                    Seleccionar
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={onClose}>Cerrar</Button>
      </ModalFooter>
    </Modal>
  );
};

export default ClientModal;
