import { createSlice } from '@reduxjs/toolkit';
import createApiInstance from '../../apiCalls/apiHook';
import { selectToken } from '../auth/authSlice';

// Puedes definir las URLs de los servicios aquí
const ORDERS_SERVICE_URL = 'http://localhost:8484'; // Ejemplo, cambiar según tu configuración

export const ordersSlice = createSlice({
  name: 'orders',
  initialState: {
    list: [],
    status: 'idle',
  },
  reducers: {
    setOrders: (state, action) => {
      state.list = action.payload;
    },
    addOrder: (state, action) => {
      state.list.push(action.payload);
    },
    updateOrder: (state, action) => {
      const index = state.list.findIndex(order => order.id === action.payload.id);
      if (index >= 0) {
        state.list[index] = action.payload;
      }
    },
  },
});

export const { setOrders, addOrder, updateOrder } = ordersSlice.actions;

const addAuthToken = (config = {}, token) => {
  if (token) {
    config.headers = {
      ...config.headers,
      'Authorization': `Bearer ${token}`,
    };
  }
  return config;
};

const fetchOrders = () => async (dispatch, getState) => {
  try {
    const state = getState();
    const token = selectToken(state);
    const api = createApiInstance(ORDERS_SERVICE_URL);
    const config = addAuthToken({}, token);
    const response = await api.get('/orders', config);
    dispatch(setOrders(response.data));
  } catch (err) {
    console.error("Error fetching orders:", err);
  }
};

// Exporta fetchOrders
export { fetchOrders };

export const confirmOrder = (orderId) => async (dispatch, getState) => {
  try {
    const state = getState();
    const token = selectToken(state);
    const api = createApiInstance(ORDERS_SERVICE_URL);
    const config = addAuthToken({}, token);
    const response = await api.put(`/orders?id=${orderId}&state=E-E`, {}, config);

    if (response.status === 200) {
      dispatch(updateOrder(response.data));
      dispatch(fetchOrders()); // Refresca la lista de pedidos
    } else {
      console.error("Error confirming order:", response.status);
    }
  } catch (err) {
    if (err.response) {
      switch (err.response.status) {
        case 404:
          console.error("Order not found:", err.response.data);
          break;
        case 409:
          console.error("Conflict updating order:", err.response.data);
          break;
        default:
          console.error("Error confirming order:", err.message);
          break;
      }
    } else {
      console.error("Error confirming order:", err.message);
    }
  }
};

export const cancelOrder = (orderId) => async (dispatch, getState) => {
  try {
    const state = getState();
    const token = selectToken(state);
    const api = createApiInstance(ORDERS_SERVICE_URL);
    const config = addAuthToken({}, token);
    const response = await api.put(`/orders?id=${orderId}&state=E-C`, {}, config);
    if (response.status === 200) {
      dispatch(updateOrder(response.data));
      dispatch(fetchOrders()); // Refresca la lista de pedidos
    } else {
      console.error("Error canceling order:", response.status);
    }
  } catch (err) {
    if (err.response) {
      switch (err.response.status) {
        case 404:
          console.error("Order not found:", err.response.data);
          break;
        case 409:
          console.error("Conflict updating order:", err.response.data);
          break;
        default:
          console.error("Error canceling order:", err.message);
          break;
      }
    } else {
      console.error("Error canceling order:", err.message);
    }
  }
};

export default ordersSlice.reducer;
