import axios from 'axios';

const createApiInstance = (baseURL) => {
  const apiInstance = axios.create({ baseURL });
  return apiInstance;
};

const createApiCall = (baseURL, token) => {
  const apiInstance = axios.create({ baseURL });

  apiInstance.interceptors.request.use((config) => {
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  });

  return apiInstance;
};

// Definir las instancias de API usando baseURLs
const apiUserServices = createApiInstance('http://localhost:8181/v1/users');
const apiInventary = createApiInstance('http://localhost:8383/v1');
const apiOrders = createApiInstance('http://localhost:8484/v1');
const apiCustomers = createApiInstance('http://localhost:8282/v1/customers');

export { createApiCall, apiUserServices, apiInventary, apiOrders, apiCustomers };
