import React, { useState, useEffect } from 'react';
import {apiOrders} from '../apiCalls/apiCalls';

const OrderForm = () => {
  const [hawas, setHawas] = useState([]);
  const [selectedHawa, setSelectedHawa] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [discount, setDiscount] = useState(0);
  const [price, setPrice] = useState(0);
  const [available, setAvailable] = useState(false);
  const [orderList, setOrderList] = useState([]);
  const [customerData, setCustomerData] = useState({ name: '', store: '' });
  const [vendorName, setVendorName] = useState('');
  const [status, setStatus] = useState('');
  const [submissionStatus, setSubmissionStatus] = useState('');

  useEffect(() => {
    // Cargar los datos iniciales del inventario si es necesario
    // Por ejemplo, podrÃ­as obtener una lista de productos disponibles al cargar el formulario
  }, []);

  const handleHawaChange = async (e) => {
    const hawa = e.target.value;
    setSelectedHawa(hawa);

    try {
      const response = await apiOrders.get(`/orders/${hawa}`);
      const { price, discount, available } = response.data;
      setPrice(price);
      setDiscount(discount);
      setAvailable(available);
    } catch (err) {
      console.error('Error fetching product data:', err);
    }
  };

  const handleAddToOrder = () => {
    if (!available) {
      alert('Product is not available.');
      return;
    }

    setOrderList([...orderList, { hawa: selectedHawa, quantity, discount, price }]);
    setSelectedHawa('');
    setQuantity(1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const orderDetails = {
      orderList,
      customerData,
      vendorName,
      status,
    };

    try {
      const response = await apiClient.post('/v1/orders', orderDetails);
      setSubmissionStatus('Order placed successfully');
    } catch (err) {
      console.error('Error placing order:', err);
      setSubmissionStatus('Error placing order');
    }
  };

  return (
    <div>
      <h2>Order Form</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            HAWA ID:
            <input type="text" value={selectedHawa} onChange={handleHawaChange} />
          </label>
        </div>
        <div>
          <label>
            Quantity:
            <input type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} />
          </label>
        </div>
        <div>
          <label>
            Discount:
            <input type="number" value={discount} onChange={(e) => setDiscount(Number(e.target.value))} disabled />
          </label>
        </div>
        <div>
          <label>
            Price:
            <input type="number" value={price} onChange={(e) => setPrice(Number(e.target.value))} disabled />
          </label>
        </div>
        <button type="button" onClick={handleAddToOrder}>Add to Order</button>
        <div>
          <label>
            Customer Name:
            <input type="text" value={customerData.name} onChange={(e) => setCustomerData({ ...customerData, name: e.target.value })} />
          </label>
        </div>
        <div>
          <label>
            Store ID:
            <input type="text" value={customerData.store} onChange={(e) => setCustomerData({ ...customerData, store: e.target.value })} />
          </label>
        </div>
        <div>
          <label>
            Vendor Name:
            <input type="text" value={vendorName} onChange={(e) => setVendorName(e.target.value)} />
          </label>
        </div>
        <div>
          <label>
            Status:
            <select value={status} onChange={(e) => setStatus(e.target.value)}>
              <option value="pending">Pending</option>
              <option value="delivered">Delivered</option>
              <option value="cancelled">Cancelled</option>
            </select>
          </label>
        </div>
        <button type="submit">Submit Order</button>
      </form>
      <div>
        <h3>Order List</h3>
        <ul>
          {orderList.map((item, index) => (
            <li key={index}>
              HAWA ID: {item.hawa}, Quantity: {item.quantity}, Discount: {item.discount}, Price: {item.price}
            </li>
          ))}
        </ul>
      </div>
      <div>
        <p>{submissionStatus}</p>
      </div>
    </div>
  );
};

export default OrderForm;
