import React, { useState, useEffect } from "react";
import {
  Label,
  FormGroup,
  Modal,
  ModalHeader,
  ModalBody,
  ModalFooter,
  Button,
  Input,
  Table,
  Pagination,
  PaginationItem,
  PaginationLink,
} from "reactstrap";
import { apiInventary } from "../../apiCalls/apiCalls";
import "../../css/ProductModal.css";

const ProductModal = ({ isOpen, onClose, onAddToOrder }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [products, setProducts] = useState([]);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [discount, setDiscount] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);

  useEffect(() => {
    if (searchTerm) {
      const fetchProducts = async () => {
        try {
          const response = await apiInventary.get(
            `/products/search?name=${searchTerm}&page=${currentPage}&size=3`
          );
          setProducts(response.data.content);
          setTotalPages(response.data.totalPages);
        } catch (err) {
          console.error("Error fetching products:", err);
        }
      };
      fetchProducts();
    }
  }, [searchTerm, currentPage]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
    setCurrentPage(0); // Reset page when search term changes
  };

  const handleSelectProduct = (product) => {
    setSelectedProduct(product);
  };

  const handleAddProductToOrder = () => {
    if (selectedProduct) {
      const orderItem = {
        hawaId: selectedProduct.id,
        nombreProducto: selectedProduct.name,
        precioLista: selectedProduct.price,
        descuento: discount,
        cantidad: quantity,
        subtotal: (selectedProduct.price - discount) * quantity,
        disponibilidad: selectedProduct.available,
      };
      onAddToOrder(orderItem);
      setSelectedProduct(null);
      setQuantity(1);
      setDiscount(0);
      onClose();
    }
  };

  const handleDiscountChange = (e) => {
    let value = Number(e.target.value);
    // Validar el descuento
    if (value < 0) {
      value = 0;
    } else if (value > 100) {
      value = 100;
    }
    setDiscount(value);
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <Modal isOpen={isOpen} toggle={onClose} className="modal-dialog-scrollable">
      <ModalHeader toggle={onClose}>Select Product</ModalHeader>
      <ModalBody className="modal-body">
        <Input
          type="text"
          placeholder="Search by product name"
          value={searchTerm}
          onChange={handleSearchChange}
        />
        <Table>
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Precio</th>
              <th>Disponibilidad</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.id}>
                <td>{product.name}</td>
                <td>{product.price}</td>
                <td>{product.available ? "Si" : "No"}</td>
                <td>
                  <Button onClick={() => handleSelectProduct(product)}>
                    Select
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
        {selectedProduct && (
          <div>
            <h5>Producto seleccionado: {selectedProduct.name}</h5>
            <FormGroup>
              <Label for="quantity">Quantity</Label>
              <Input
                id="quantity"
                type="number"
                min="1"
                placeholder="Cantidad"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
              />
            </FormGroup>
            <FormGroup>
              <Label for="discount">Discount</Label>
              <Input
                id="discount"
                type="number"
                min="0"
                max="100"
                placeholder="Discount"
                value={discount}
                onChange={handleDiscountChange} // Actualiza la funciÃ³n para manejar el cambio en el descuento
              />
            </FormGroup>

            <Button onClick={handleAddProductToOrder}>Add to Order</Button>
            <br/>
            <br/>
          </div>
        )}
        <Pagination>
          <PaginationItem disabled={currentPage === 0}>
            <PaginationLink
              previous
              onClick={() => handlePageChange(currentPage - 1)}
            />
          </PaginationItem>
          {Array.from({ length: totalPages }, (_, index) => (
            <PaginationItem key={index} active={index === currentPage}>
              <PaginationLink onClick={() => handlePageChange(index)}>
                {index + 1}
              </PaginationLink>
            </PaginationItem>
          ))}
          <PaginationItem disabled={currentPage === totalPages - 1}>
            <PaginationLink
              next
              onClick={() => handlePageChange(currentPage + 1)}
            />
          </PaginationItem>
        </Pagination>
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={onClose}>
          Close
        </Button>
      </ModalFooter>
    </Modal>
  );
};

export default ProductModal;
