import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchOrders } from '../../features/orders/ordersSlice';
import OrderDetail from './OrderDetail'; // Asegúrate de tener un componente para mostrar el detalle del pedido

const OrderList = () => {
  const dispatch = useDispatch();
  const orders = useSelector((state) => state.orders.list);
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    dispatch(fetchOrders());
  }, [dispatch]);

  const handleSelectOrder = (order) => {
    setSelectedOrder(order);
  };

  const handleConfirmOrder = (orderId) => {
    // Llama a la API para confirmar el pedido
    // Puedes despachar una acción Redux para actualizar el estado
  };

  const handleCancelOrder = (orderId) => {
    // Llama a la API para cancelar el pedido
    // Puedes despachar una acción Redux para actualizar el estado
  };

  return (
    <div>
      <ul>
        {orders.length > 0 ? (
          orders.map((order) => (
            <li key={order.id}>
              {order.productId} - {order.quantity}
              <button onClick={() => handleSelectOrder(order)}>Ver Detalles</button>
              <button onClick={() => handleConfirmOrder(order.id)}>Confirmar</button>
              <button onClick={() => handleCancelOrder(order.id)}>Cancelar</button>
            </li>
          ))
        ) : (
          <li>No hay pedidos disponibles</li>
        )}
      </ul>
      {selectedOrder && <OrderDetail order={selectedOrder} />}
    </div>
  );
};

export default OrderList;
