import { useState, useEffect } from 'react';
import axios from 'axios';

// Supongamos que este hook se encarga de obtener la URL de Eureka
const useEurekaServices = () => {
  const [services, setServices] = useState({});

  useEffect(() => {
    // Aquí iría la lógica para consultar Eureka y obtener los servicios
    const fetchServices = async () => {
      try {
        const response = await axios.get('http://localhost:8761/eureka/apps');
        // Procesa la respuesta y ajusta según sea necesario
        const services = {}; // Ajusta esta lógica según la respuesta de Eureka
        setServices(services);
      } catch (error) {
        console.error('Error fetching services from Eureka:', error);
      }
    };

    fetchServices();
  }, []);

  return services;
};

export default useEurekaServices; // Asegúrate de exportar correctamente
