import React, { useState, useEffect } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Spinner, Alert } from "reactstrap";
import { apiOrders } from "../../apiCalls/apiCalls"; // Asegúrate de que la ruta sea correcta

const StoreModal = ({ isOpen, onClose, onSelect }) => {
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await apiOrders.get('/stores'); // Cambia la URL según sea necesario
        setStores(response.data);
        setLoading(false);
      } catch (err) {
        setError("Error fetching stores");
        setLoading(false);
        console.error("Error fetching stores:", err);
      }
    };

    if (isOpen) {
      fetchStores();
    }
  }, [isOpen]);

  return (
    <Modal isOpen={isOpen} toggle={onClose}>
      <ModalHeader toggle={onClose}>Seleccionar Tienda</ModalHeader>
      <ModalBody>
        {loading ? (
          <div className="text-center">
            <Spinner color="primary" />
          </div>
        ) : error ? (
          <Alert color="danger">{error}</Alert>
        ) : (
          <ul>
            {stores.length > 0 ? (
              stores.map((store) => (
                <li key={store.id}>
                  <Button
                    color="primary"
                    onClick={() => {
                      onSelect(store);
                      onClose();
                    }}
                  >
                    {store.name}
                  </Button>
                </li>
              ))
            ) : (
              <p>No hay tiendas disponibles.</p>
            )}
          </ul>
        )}
      </ModalBody>
      <ModalFooter>
        <Button color="secondary" onClick={onClose}>Cerrar</Button>
      </ModalFooter>
    </Modal>
  );
};

export default StoreModal;
