import axios from 'axios';

const apiUserServices = axios.create({
  baseURL: 'http://localhost:8181/v1/users',
});

const apiInventary = axios.create({
  baseURL: 'http://localhost:8383/v1',
});

const apiOrders = axios.create({
  baseURL: 'http://localhost:8484/v1',
});

const apiCustomers = axios.create({
  baseURL: 'http://localhost:8282/v1/customers',
});

// Función para obtener el token de localStorage
const getToken = () => {
  return localStorage.getItem('token');
};

// Configura el interceptor para incluir el token en los encabezados
apiUserServices.interceptors.request.use((config) => {
  const token = getToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

apiInventary.interceptors.request.use((config) => {
  const token = getToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

apiOrders.interceptors.request.use((config) => {
  const token = getToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

apiCustomers.interceptors.request.use((config) => {
  const token = getToken();
  if (token) {
    config.headers.Authorization = `Bearer ${token}`;
  }
  return config;
});

export { apiUserServices, apiInventary, apiOrders, apiCustomers };
