import React, { useState } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter, Button, Input, Table, FormGroup, Label, FormFeedback } from "reactstrap";
import { apiCustomers } from "../../apiCalls/apiCalls";

const ClientModal = ({ isOpen, onClose, onSelect }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [clients, setClients] = useState([]);
  const [selectedClient, setSelectedClient] = useState(null);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    if (searchTerm.trim() === "") {
      setError("Ingrese un término de búsqueda.");
      return;
    }

    try {
      const response = await apiCustomers.get(`/search?term=${searchTerm}`);
      if (response.data.length === 0) {
        setError("No hay registros.");
        setClients([]);
      } else {
        setClients(response.data);
        setError("");
      }
    } catch (err) {
      console.error("Error fetching clients:", err);
      setError("Error al buscar clientes.");
    }
  };

  const handleSelectClient = (client) => {
    setSelectedClient(client);
  };

  const handleRegisterNewClient = () => {
    // Funcionalidad para registrar un nuevo cliente
    // Puedes mostrar otro modal o redirigir a una página de registro
  };

  return (
    <Modal isOpen={isOpen} toggle={onClose} size="lg">
      <ModalHeader toggle={onClose}>Seleccionar Cliente</ModalHeader>
      <ModalBody>
        <FormGroup>
          <Label for="search">Buscar Cliente</Label>
          <Input
            id="search"
            type="text"
            placeholder="Nombre o correo"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Button color="primary" onClick={handleSearch} className="mt-2">
            Buscar
          </Button>
          {error && <div className="text-danger mt-2">{error}</div>}
        </FormGroup>
        {clients.length > 0 && (
          <Table striped>
            <thead>
              <tr>
                <th>Nombre</th>
                <th>Correo</th>
                <th>Teléfono</th>
                <th>Acción</th>
              </tr>
            </thead>
            <tbody>
              {clients.map((client) => (
                <tr key={client.id}>
                  <td>{client.name}</td>
                  <td>{client.email}</td>
                  <td>{client.phone}</td>
                  <td>
                    <Button onClick={() => handleSelectClient(client)} color="success">
                      Seleccionar
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        )}
        <Button color="secondary" onClick={handleRegisterNewClient} className="mt-2">
          Registrar Nuevo Cliente
        </Button>
      </ModalBody>
      <ModalFooter>
        <Button color="primary" onClick={() => { onSelect(selectedClient); setSelectedClient(null); }}>
          Aceptar
        </Button>
        <Button color="secondary" onClick={onClose}>
          Cancelar
        </Button>
      </ModalFooter>
    </Modal>
  );
};

export default ClientModal;
