import axios from 'axios';
import { Orders } from "../cons/urls";
import store from '../store/store'; // Importa el store

const apiHook = axios.create({
  baseURL: Orders, 
  headers: {
    'Content-Type': 'application/json',
  },
});

// Interceptor para agregar el token a las solicitudes
apiHook.interceptors.request.use(
  (config) => {
    const state = store.getState(); // Obtén el estado del store
    const token = state.auth.token; // Accede al token desde el estado
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

export default apiHook;
